 function init(){
	
	var timeSlide 	= 1000;
	var timeWait	= 6000;
 
	var slideShow			= document.getElementsByClassName("slideshow")[0]
 	var slider 				= document.getElementsByClassName("slider")[0] 
	var slideContainer 		= slideShow.getElementsByClassName("slide-container")[0]
	interval = true
	var nowImg = 1 ;
	var imgCount 		= slideShow.getElementsByClassName("slide").length;
	var slideshowWidth  = slideShow.clientWidth
 
	for(var i = 1 ; i < imgCount+1; i++ ){
		var div = document.createElement("div");
		div.id = "k"+i;
		if(i==1) div.className = 'current'  
		document.getElementsByClassName('slider')[0].appendChild(div);
	}
	
	for(var i = 0 ; i < imgCount ; i++){
		slideShow.getElementsByClassName("slide")[i].style.width = (100/imgCount)+"%";
	}
	
	slideContainer.style.width = slideContainer.style.minWidth = slideContainer.style.maxWidth = (imgCount*100)+"%";	
	
	function createInterval(interval,nowImg){
			if(interval){
				interval = setInterval(function(){
				var num = parseInt(nowImg) + 1
				
				for(var i = 0 ; i< slider.getElementsByTagName("div").length ; i++){
					slider.getElementsByTagName("div")[i].className = ''}
				if(nowImg >= imgCount) {nowImg = 0 ; num=1;}
				
				var current = document.getElementById("k" + num)
				current.className = 'current'
				
				document.getElementsByClassName("slide-container")[0].style.marginLeft = "-" + (100*nowImg)+"%";
				nowImg++;
				
			}, (timeSlide+ timeWait));
			return interval;		
			}
	}
	
	function deleteInterval(interval){
		if(interval){
			clearInterval(interval);
			return false;
		}	
	}
	
	slider.onclick = function(e){
		if(e.target.id){
		
			interval = deleteInterval(interval);
			interval = setTimeout(function(){interval = createInterval(interval,place);}, (timeSlide+ timeWait));
			
			for(var i = 0 ; i< slider.getElementsByTagName("div").length ; i++){
				slider.getElementsByTagName("div")[i].className = ''
			}
			if(e.target.className !== "current") e.target.className = "current";
			
			place = e.target.id.charAt(1)
			document.getElementById("slide-container").style.marginLeft = "-" + (100*(place-1))+"%"			
		}
	};	

	interval = createInterval(interval,nowImg);
	
 };
 document.onload = init();